mapper.insert:
INSERT IGNORE INTO geopoint (`id`, `lon`, `lat`)
VALUES

	(
		IFNULL(
			(
				SELECT
					id
				FROM
					(
						SELECT
							id
						FROM
							geopoint
						WHERE
							lon = ${item.geoPoint.lon}
						AND lat = ${item.geoPoint.lat}
					) AS x
			),
			(SELECT UUID() AS id)
		),
		${item.geoPoint.lon},
		${item.geoPoint.lat}
	)
